function Level(){
    return(
        <div>
            <br></br>
            <label for="Game_level" className="Game_lbl">Choose a Game Level</label>
            <select id="Game_Level" className="btn btn-outline-info btn-rounded waves-effect" onChange={()=>ChangeLevel(document.getElementById("Game_level").value)}>
                
                <option className="btn-outline-secondary" value="level1">Easy</option>
                <option className="btn-outline-secondary" value="level2">Medium</option>
                <option className="btn-outline-secondary" value="level3">Difficult</option>
            </select>
        </div>
    );
    }
function ChangeLevel(Level){
    ///////////Reset Display///////////
    document.getElementById('game_alert').innerHTML="";
    document.getElementById('game_alert').className="alert_class_hidden";
    document.getElementById("score").innerHTML="000";
    document.getElementById("timer").innerHTML=("00:00");
    for (let id of window.all_act_ids){
        document.getElementById(id).className="box";
    }
    for (let id of window.snake_speed){
        document.getElementById(id).className="box";
    }
    switch (Level) {
        case 'Level1':
            window.snake_speed=500;
            break;
        case 'Level2':
            window.snake_speed=1000;
            break;
        case 'Level3':
            window.snake_speed=2000;
            break;
        default:
            window.snake_speed=[];
    }
    for (let id of window.obstacle_pattern){
        document.getElementById(id).className="level_box";
    }
}
    export default Level;
